using System.Diagnostics;
using ServiceStack.DataAnnotations;
using ServiceStack.OrmLite;

namespace SSVarCharMaxBug
{
    class Person
    {
        [AutoIncrement]
        public int Id { get; set; }

        [CustomField("VARCHAR(MAX)")]
        public string Name { get; set; }
    }

    class Program
    {
        static void Main(string[] args)
        {
            var connectionString = "Server=.; Database=SSVarCharMaxBug; Trusted_Connection=True; Application Name=SSVarCharMaxBug";

            var ormLiteConnectionFactory = new OrmLiteConnectionFactory(
                  connectionString,
                  SqlServerDialect.Provider);

            OrmLiteConfig.DialectProvider.GetStringConverter().UseUnicode = false;

            using (var db = ormLiteConnectionFactory.OpenDbConnection())
            {
                db.DropAndCreateTable<Person>();

                var name = "a" + new string('b', 8000) + "c";
                
                db.Insert(new Person
                {
                    Name = name
                });

                Debug.Assert(db.Select<Person>().Count == 1);

                var people = db.Select<Person>(p => p.Name == name);

/* 
exec sp_executesql N'SELECT "Id", "Name" 
FROM "Person"
WHERE ("Name" = @0)',N'@0 
varchar(8000)',@0=''
*/

                // Fails as generated SQL is varchar(8000), it should be varchar(MAX), the string is truncated
                // When UseUnicode is `true` nvarchar(MAX) is used so it's successful.

                Debug.Assert(people.Count == 1);
            }
        }
    }
}
